import React, {Fragment, useEffect, useLayoutEffect, useState} from "react";
import useToken from "./useToken";
import Login from "./Login";
import App from "../App";



function Content() {
    const [books, setBooks] = useState(true);

    const { token, setToken } = useToken();

    useLayoutEffect(() => {
        getBooks();
    });

    // const Books = () => {
    //     const [books, setBooks] = useState([]);
    //     const [bookName, setBookName] = useState("");
    //     const [amount, setAmount] = useState("");
    //
    //     const { token, setToken } = useToken();
    //
    //     useLayoutEffect(() => {
    //         getBooks();
    //     });

    function getBooks() {
        fetch('http://localhost:8083/api/books/', {
            headers: {
                'Authorization': token
            }
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                setBooks(data.json);
                console.log(data);
            });
    }

    function createBook() {
        let bookName = prompt('Enter text');
        let amount = prompt('Enter amount');//
        fetch('http://localhost:8083/api/books/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': token
            },
            body: JSON.stringify({bookName, amount}),
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data.json());
                getBooks();
            });
    }

    function deleteBook() {
        let id = prompt('Enter book ID');
        fetch(`http://localhost:8083/api/books/${id}`, {
            method: 'DELETE',
            headers: {
                'Authorization': token
            }
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data.json());
                getBooks();

            });
    }

    function updateBook() {
        let id = prompt('Enter book ID');
        let bookName= prompt('Enter new text');
        let amount= prompt('Enter new amount');//
        fetch('http://localhost:8083/api/books/', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': token
            },
            body: JSON.stringify({
                id,
                bookName,
                amount
            }),
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data.json());
                getBooks();
            });
    }

    function addBook() {
        let bookName = prompt('Enter text');
        let amount = prompt('Enter amount');//
        fetch('http://localhost:8083/api/books/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': token
            },
            body: JSON.stringify({bookName, amount}),
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data.json());
                getBooks();
            });
    }


    // if(!token) {
    //     return <Login setToken={setToken}/>
    // }
    return (

        <div>

            {books}
            <table>
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Text</th>
                    <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {[books].map(book =>
                  <tr>
                    <td>{book.id}</td>
                    <td>{book.bookName}</td>
                      <td>{book.amount}</td>
                  </tr>)}
              </tbody>
            </table>

            <br />
            <button onClick={createBook}>Add book</button>
            <br />
            <button onClick={deleteBook}>Delete book</button>
            <br />
            <button onClick={updateBook}>Update book</button>
        </div>
    );
}

export default Content;
