import React, {useLayoutEffect, useState} from "react";
import useToken from "./useToken";
import Login from "./Login";
import App from "../App";

function Content() {
    const [books, setBooks] = useState(true);

    const { token, setToken } = useToken();

    useLayoutEffect(() => {
        getBooks();
    });

    function getBooks() {
        fetch('http://localhost:8083/api/books/', {
            headers: {
                'Authorization': token
            }
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                setBooks(data);
            });
    }

    function createBook() {
        let bookName = prompt('Enter text');
        let amount = prompt('Enter amount');//
        fetch('http://localhost:8083/api/books/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': token
            },
            body: JSON.stringify({bookName, amount}),
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data);
                getBooks();
            });
    }

    function deleteBook() {
        let id = prompt('Enter book ID');
        fetch(`http://localhost:8083/api/books/${id}`, {
            method: 'DELETE',
            headers: {
                'Authorization': token
            }
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data);
                getBooks();
            });
    }

    function updateBook() {
        let id = prompt('Enter book ID');
        let bookName= prompt('Enter new text');
        let amount= prompt('Enter new amount');//
        fetch('http://localhost:8083/api/books/', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': token
            },
            body: JSON.stringify({
                id,
                bookName,
                amount
            }),
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data);
                getBooks();
            });
    }
    // if(!token) {
    //     return <Login setToken={setToken}/>
    // }
    return (

        <div>


            {/*<table>*/}
            {/*  <thead>*/}
            {/*    <tr>*/}
            {/*      <th>Id</th>*/}
            {/*      <th>Text</th>*/}
            {/*    </tr>*/}
            {/*  </thead>*/}
            {/*  <tbody>*/}
            {/*    {JSON.parse(books).map(item =>*/}
            {/*      <tr>*/}
            {/*        <td>{item.id}</td>*/}
            {/*        <td>{item.bookText}</td>*/}
            {/*      </tr>)}*/}
            {/*  </tbody>*/}
            {/*</table>*/}
            {books}
            <br />
            <button onClick={createBook}>Add book</button>
            <br />
            <button onClick={deleteBook}>Delete book</button>
            <br />
            <button onClick={updateBook}>Update book</button>
        </div>
    );
}

export default Content;
