import React, {useEffect, useLayoutEffect, useRef, useState} from 'react';
import {BrowserRouter, Route, Routes} from "react-router-dom";
import Login from './components/Login';

import Register from "./components/register";
import useToken from './components/useToken';
import Content from "./components/content";




function App() {
    const [examples, setExamples] = useState(true);

    const { token, setToken } = useToken();

    useLayoutEffect(() => {
        getExamples();
    });

    function getExamples() {
        fetch('http://localhost:8083/api/examples/', {
            headers: {
                'Authorization': token
            }
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                setExamples(data);
            });
    }

    function createExample() {
        let exampleText = prompt('Enter text');
        fetch('http://localhost:8083/api/examples/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': token
            },
            body: JSON.stringify({exampleText}),
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data);
                getExamples();
            });
    }

    function deleteExample() {
        let id = prompt('Enter example ID');
        fetch(`http://localhost:8083/api/examples/${id}`, {
            method: 'DELETE',
            headers: {
                'Authorization': token
            }
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data);
                getExamples();
            });
    }

    function updateExample() {
        let id = prompt('Enter example ID');
        let exampleText = prompt('Enter new text');
        fetch('http://localhost:8083/api/examples/', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': token
            },
            body: JSON.stringify({
                id,
                exampleText
            }),
        })
            .then(response => {
                return response.text();
            })
            .then(data => {
                alert(data);
                getExamples();
            });
    }


    if(!token) {
        return (
            <div className="App">
                <header className="App-header">
                    <BrowserRouter>
                        <Routes>
                            <Route path="/*" element={<Login setToken={setToken}/>}/>
                            <Route path="/register" element={<Register/>}/>
                        </Routes>
                    </BrowserRouter>
                </header>
            </div>)
    }
    return (
        <div className="App">
            <header className="App-header">
                <Provider store={store}>
                    <BrowserRouter>
                        <Routes>
                            <Route path="/" element={<Content quote={"Content"}/>}/>
                        </Routes>
                    </BrowserRouter>
                </Provider>
            </header>
        </div>
    );
}

export default App;
